// src/components/Login.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'; // If you're using axios
import '../style.css';

const Login = ({ onLogin, message }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  
  // 1) Add new state for error message
  const [errorMsg, setErrorMsg] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Clear previous error (optional)
    setErrorMsg('');

    try {
      // 2) Example login request to your backend
      //    Adjust URL and data as needed
      const response = await axios.post('/api/login', { username, password });

      // 3) Check the response from your server
      if (response.data.error === 'USER_NOT_FOUND') {
        setErrorMsg('User does not exist');
      } else if (response.data.error === 'INCORRECT_PASSWORD') {
        setErrorMsg('This login attempt failed due to incorrect password');
      } else {
        // Assume success if no error
        if (onLogin) {
          onLogin({ username, password });
        }
      }
    } catch (err) {
      console.error(err);
      // Fallback message if the server request fails
      setErrorMsg('An error occurred while logging in.');
    }
  };

  return (
    <div className="container">
      <h2>Login</h2>
      {message && <p style={{ color: 'red' }}>{message}</p>}

      {/* 4) Floating error message if errorMsg is not empty */}
      {errorMsg && (
        <div className="floating-error">
          {errorMsg}
        </div>
      )}
      {registrationSuccess && (
        <div className="floating-success">
          Registration successfully completed!
        </div>
)}
      <form onSubmit={handleSubmit}>
        <input 
          type="text"
          name="username"
          placeholder="Username"
          required
          value={username}
          onChange={e => setUsername(e.target.value)}
        />
        <input 
          type="password"
          name="password"
          placeholder="Password"
          required
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
      <p>Don't have an account? <Link to="/register">Register here</Link></p>
    </div>
  );
};

export default Login;
